import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";

function flattenExchanges(pricesJson) {
  // Suppose pricesJson has a structure:
  // {
  //   "updated": "2025-01-26T02:42:40.406188Z",
  //   "exchanges": [
  //     {
  //       "haveCurrency": "Chaos Orb",
  //       "wantCurrency": "Divine Orb",
  //       "lastUpdated": "...",
  //       "offers": [...],
  //       "competingTrades": [...]
  //     },
  //     ...
  //   ]
  // }
  //
  // We'll create 2 rows (first offer, first competing) per exchange.
  let rowId = 1;
  const rows = [];

  pricesJson.exchanges.forEach((exchange) => {
    const { haveCurrency, wantCurrency, lastUpdated, offers, competingTrades } = exchange;

    // If there's at least one offer:
    if (offers && offers.length > 0) {
      const firstOffer = offers[0];
      rows.push({
        id: rowId++,
        rowType: "offer",
        haveCurrency,
        wantCurrency,
        lastUpdated,
        haveAmount: firstOffer.haveAmount,
        wantAmount: firstOffer.wantAmount,
        stock: firstOffer.stock
      });
    }

    // If there's at least one competing trade:
    if (competingTrades && competingTrades.length > 0) {
      const firstCompeting = competingTrades[0];
      rows.push({
        id: rowId++,
        rowType: "competing",
        haveCurrency,
        wantCurrency,
        lastUpdated,
        haveAmount: firstCompeting.haveAmount,
        wantAmount: firstCompeting.wantAmount,
        stock: firstCompeting.stock
      });
    }
  });

  return rows;
}

export default function PriceDataGrid() {
  const [rows, setRows] = useState([]);
  const [currencyIconMap, setCurrencyIconMap] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // We'll fetch both JSON files in parallel
    Promise.all([
      fetch("/currencyOverview.json").then((r) => r.json()),
      fetch("/prices.json").then((r) => r.json())
    ])
      .then(([ninjaData, pricesData]) => {
        // 1. Build icon map from ninjaData
        //    e.g., { "Chaos Orb": "iconURL", "Divine Orb": "iconURL", ...}
        const map = {};
        if (ninjaData.currencyDetails) {
          ninjaData.currencyDetails.forEach((c) => {
            map[c.name] = c.icon;
          });
        }

        // 2. Flatten the prices.json data
        const flattenedRows = flattenExchanges(pricesData);

        // 3. Update state
        setCurrencyIconMap(map);
        setRows(flattenedRows);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching JSON files:", err);
        setLoading(false);
      });
  }, []);

  // Define columns, referencing the icon map in 'haveCurrency'
  const columns = [
    {
      field: "haveCurrency",
      headerName: "Have Currency",
      width: 200,
      renderCell: (params) => {
        const currencyName = params.value;
        const iconUrl = currencyIconMap[currencyName];
        return (
          <div style={{ display: "flex", alignItems: "center" }}>
            {iconUrl && (
              <img
                src={iconUrl}
                alt={currencyName}
                style={{ width: 24, height: 24, marginRight: 8 }}
              />
            )}
            {currencyName}
          </div>
        );
      }
    },
    { field: "wantCurrency", headerName: "Want Currency", width: 130 },
    { field: "lastUpdated", headerName: "Last Updated", width: 180 },
    { field: "rowType", headerName: "Type", width: 90 },
    { field: "haveAmount", headerName: "Have Amt", type: "number", width: 100 },
    { field: "wantAmount", headerName: "Want Amt", type: "number", width: 100 },
    { field: "stock", headerName: "Stock", type: "number", width: 100 }
    // Add more columns if needed (e.g. ninjaPrice)
  ];

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ height: 800, width: "100%", marginTop: "1rem" }}>
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={10}
        rowsPerPageOptions={[5, 10, 25]}
      />
    </div>
  );
}
