import React, { useEffect, useState } from "react";
import { AppBar, Toolbar, Typography, Button, CircularProgress } from "@mui/material";

function HeaderBar() {
  const [divineOrbPrice, setDivineOrbPrice] = useState(null);
  const [lastModified, setLastModified] = useState(null);
  const [isActive, setIsActive] = useState(false);
  const [loading, setLoading] = useState(false);

  const fetchStatus = async () => {
    setLoading(true);
    try {
      // Fetch the currencyOverview.json metadata (for last modified date)
      const response = await fetch("/currencyOverview.json", { method: "HEAD" });
      const lastModified = response.headers.get("last-modified");
      const lastModifiedDate = new Date(lastModified);

      // Fetch the full JSON data
      const jsonResponse = await fetch("/currencyOverview.json");
      const data = await jsonResponse.json();

      // Extract Divine Orb price
      const divineOrb = data.lines.find(
        (line) => line.currencyTypeName === "Divine Orb"
      );
      const price = divineOrb ? divineOrb.chaosEquivalent : "N/A";

      // Check if the file is "active"
      const now = new Date();
      const sixMinutesAgo = new Date(now.getTime() - 6 * 60 * 1000);
      const isActive = lastModifiedDate > sixMinutesAgo;

      // Update state
      setDivineOrbPrice(price);
      setLastModified(lastModifiedDate);
      setIsActive(isActive);
    } catch (error) {
      console.error("Error fetching status:", error);
    }
    setLoading(false);
  };

  // Poll every 60 seconds
  useEffect(() => {
    fetchStatus(); // Fetch immediately on component mount

    const interval = setInterval(fetchStatus, 60000); // Poll every 60 seconds
    return () => clearInterval(interval); // Clean up on unmount
  }, []);

  return (
    <AppBar position="static">
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          Faustus Price Checker
        </Typography>
        {loading ? (
          <CircularProgress size={24} sx={{ color: "white", mr: 2 }} />
        ) : (
          <>
            <Typography variant="body1" sx={{ mr: 2 }}>
              Divine Orb Price: {divineOrbPrice || "Loading..."} Chaos
            </Typography>
            <Typography variant="body2" sx={{ mr: 2 }}>
              Status: {isActive ? "Active ðŸŸ¢" : "Inactive ðŸ”´"}
            </Typography>
          </>
        )}
        <Button color="inherit" onClick={fetchStatus}>
          Refresh
        </Button>
      </Toolbar>
    </AppBar>
  );
}

export default HeaderBar;
