function OffersTable({ offers }) {
  if (!offers || offers.length === 0) {
    return <div>No offers available</div>;
  }

  return (
    <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "0.5rem" }}>
      <thead>
        <tr>
          <th style={{ border: "1px solid #ccc" }}>Have Amount</th>
          <th style={{ border: "1px solid #ccc" }}>Want Amount</th>
          <th style={{ border: "1px solid #ccc" }}>Stock</th>
        </tr>
      </thead>
      <tbody>
        {offers.map((offer, i) => (
          <tr key={i}>
            <td style={{ border: "1px solid #ccc", textAlign: "center" }}>
              {offer.haveAmount}
            </td>
            <td style={{ border: "1px solid #ccc", textAlign: "center" }}>
              {offer.wantAmount}
            </td>
            <td style={{ border: "1px solid #ccc", textAlign: "center" }}>
              {offer.stock}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

function ExchangeCard({ exchange }) {
  const { haveCurrency, wantCurrency, lastUpdated, offers, competingTrades } = exchange;

  return (
    <div style={{ border: "1px solid #ccc", padding: "1rem", marginBottom: "1rem" }}>
      <h3>{haveCurrency} â†’ {wantCurrency}</h3>
      <p><strong>Last Updated:</strong> {lastUpdated}</p>

      <h4>Offers</h4>
      <OffersTable offers={offers} />

      <h4>Competing Trades</h4>
      <OffersTable offers={competingTrades} />
    </div>
  );
}

function PoEExchanges() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("/prices.json")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((json) => setData(json))
      .catch((err) => setError(err.message));
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ maxWidth: "800px", margin: "auto" }}>
      <h2>Path of Exile Exchange Data</h2>
      <p>Last Updated: {data.updated}</p>

      {data.exchanges.map((exchange, idx) => (
        <ExchangeCard key={idx} exchange={exchange} />
      ))}
    </div>
  );
}

export default PoEExchanges;
