import React, { useEffect, useState } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Pagination,
  PaginationContent,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious,
} from "@/components/ui/pagination";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

function flattenExchanges(pricesJson) {
  let rowId = 1;
  const rows = [];

  pricesJson.exchanges.forEach((exchange) => {
    const { haveCurrency, wantCurrency, lastUpdated, offers, competingTrades } = exchange;

    if (offers?.length > 0) {
      const firstOffer = offers[0];
      rows.push({
        id: rowId++,
        rowType: "offer",
        haveCurrency,
        wantCurrency,
        lastUpdated,
        haveAmount: firstOffer.haveAmount,
        wantAmount: firstOffer.wantAmount,
        stock: firstOffer.stock
      });
    }

    if (competingTrades?.length > 0) {
      const firstCompeting = competingTrades[0];
      rows.push({
        id: rowId++,
        rowType: "competing",
        haveCurrency,
        wantCurrency,
        lastUpdated,
        haveAmount: firstCompeting.haveAmount,
        wantAmount: firstCompeting.wantAmount,
        stock: firstCompeting.stock
      });
    }
  });

  return rows;
}

const PriceDataGrid = () => {
  const [rows, setRows] = useState([]);
  const [currencyIconMap, setCurrencyIconMap] = useState({});
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 10;

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch currency overview
        const overviewRes = await fetch("/currencyOverview.json");
        if (!overviewRes.ok) throw new Error(`Failed to fetch currencyOverview.json: ${overviewRes.status}`);
        const ninjaData = await overviewRes.json();

        // Build icon map
        const map = {};
        if (ninjaData.currencyDetails) {
          ninjaData.currencyDetails.forEach((c) => {
            map[c.name] = c.icon;
          });
        }
        setCurrencyIconMap(map);

        // Fetch prices
        const pricesRes = await fetch("/faustusPrices.json");
        if (!pricesRes.ok) throw new Error(`Failed to fetch prices.json: ${pricesRes.status}`);
        const pricesData = await pricesRes.json();

        const flattenedRows = flattenExchanges(pricesData);
        setRows(flattenedRows);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const paginatedRows = rows.slice(
    (currentPage - 1) * rowsPerPage,
    currentPage * rowsPerPage
  );
  
  const totalPages = Math.ceil(rows.length / rowsPerPage);

  if (loading) {
    return <div className="flex items-center justify-center p-4">Loading data...</div>;
  }

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle>Price Data</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="rounded-md border">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Have Currency</TableHead>
                <TableHead>Want Currency</TableHead>
                <TableHead>Last Updated</TableHead>
                <TableHead>Type</TableHead>
                <TableHead className="text-right">Have Amt</TableHead>
                <TableHead className="text-right">Want Amt</TableHead>
                <TableHead className="text-right">Stock</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {paginatedRows.map((row) => (
                <TableRow key={row.id}>
                  <TableCell className="flex items-center gap-2">
                    {currencyIconMap[row.haveCurrency] && (
                      <img
                        src={currencyIconMap[row.haveCurrency]}
                        alt={row.haveCurrency}
                        className="h-6 w-6"
                      />
                    )}
                    {row.haveCurrency}
                  </TableCell>
                  <TableCell>{row.wantCurrency}</TableCell>
                  <TableCell>{row.lastUpdated}</TableCell>
                  <TableCell>{row.rowType}</TableCell>
                  <TableCell className="text-right">{row.haveAmount}</TableCell>
                  <TableCell className="text-right">{row.wantAmount}</TableCell>
                  <TableCell className="text-right">{row.stock}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>

        <Pagination className="mt-4">
          <PaginationContent>
            <PaginationItem>
              <PaginationPrevious 
                onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
                className={currentPage === 1 ? "pointer-events-none opacity-50" : "cursor-pointer"}
              />
            </PaginationItem>
            {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
              <PaginationItem key={page}>
                <PaginationLink
                  onClick={() => setCurrentPage(page)}
                  isActive={currentPage === page}
                  className="cursor-pointer"
                >
                  {page}
                </PaginationLink>
              </PaginationItem>
            ))}
            <PaginationItem>
              <PaginationNext
                onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
                className={currentPage === totalPages ? "pointer-events-none opacity-50" : "cursor-pointer"}
              />
            </PaginationItem>
          </PaginationContent>
        </Pagination>
      </CardContent>
    </Card>
  );
};

export default PriceDataGrid;
