function flattenExchanges(priceData) {
    let rowId = 1;
    const rows = [];
  
    priceData.exchanges.forEach((exchange) => {
      const {
        haveCurrency,
        wantCurrency,
        lastUpdated,
        offers,
        competingTrades
      } = exchange;
  
      // If there's at least one offer:
      if (offers && offers.length > 0) {
        const firstOffer = offers[0];
        rows.push({
          id: rowId++,
          rowType: "offer",
          haveCurrency,
          wantCurrency,
          lastUpdated,
          haveAmount: firstOffer.haveAmount,
          wantAmount: firstOffer.wantAmount,
          stock: firstOffer.stock
        });
      }
  
      // If there's at least one competing trade:
      if (competingTrades && competingTrades.length > 0) {
        const firstCompeting = competingTrades[0];
        rows.push({
          id: rowId++,
          rowType: "competing",
          haveCurrency,
          wantCurrency,
          lastUpdated,
          haveAmount: firstCompeting.haveAmount,
          wantAmount: firstCompeting.wantAmount,
          stock: firstCompeting.stock
        });
      }
    });
  
    return rows;
  }  

function PriceDataGrid() {
  const rows = flattenExchanges(priceData);

  return (
    <div style={{ height: 1000, width: "100%", marginTop: "1rem" }}>
      <DataGrid
        rows={rows}
        columns={columns}
        sortingMode="server"
        pageSize={10}       // how many rows per page
        rowsPerPageOptions={[5, 10, 25]}
        disableSelectionOnClick
      />
    </div>
  );
}

