import React, { useEffect, useState } from "react";
import { DataGrid } from "@mui/x-data-grid";

/**
 * Flatten logic for prices.json, which looks like:
 * {
 *   "updated": "2025-01-26T02:42:40.406188Z",
 *   "exchanges": [
 *       {
 *         "haveCurrency": "...",
 *         "wantCurrency": "...",
 *         "lastUpdated": "...",
 *         "offers": [...],
 *         "competingTrades": [...]
 *       },
 *       ...
 *   ]
 * }
 */
function flattenExchanges(pricesJson) {
  if (!pricesJson || !pricesJson.exchanges) {
    console.error("Invalid data structure received:", pricesJson);
    return [];
  }

  let rowId = 1;
  const rows = [];

  pricesJson.exchanges.forEach((exchange) => {
    const { haveCurrency, wantCurrency, lastUpdated, offers, competingTrades } = exchange;

    // If there's at least one offer, push the first
    if (offers && offers.length > 0) {
      const firstOffer = offers[0];
      rows.push({
        id: rowId++,
        rowType: "offer",
        haveCurrency,
        wantCurrency,
        lastUpdated,
        haveAmount: firstOffer.haveAmount,
        wantAmount: firstOffer.wantAmount,
        stock: firstOffer.stock
      });
    }

    // If there's at least one competing, push the first
    if (competingTrades && competingTrades.length > 0) {
      const firstCompeting = competingTrades[0];
      rows.push({
        id: rowId++,
        rowType: "competing",
        haveCurrency,
        wantCurrency,
        lastUpdated,
        haveAmount: firstCompeting.haveAmount,
        wantAmount: firstCompeting.wantAmount,
        stock: firstCompeting.stock
      });
    }
  });

  return rows;
}

export default function PriceDataGrid() {
  const [rows, setRows] = useState([]);
  const [currencyIconMap, setCurrencyIconMap] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // 1) First fetch: currencyOverview.json (for icon data)
    fetch("/faustus-price-checker/currencyOverview.json")
      .then((res) => {
        if (!res.ok) {
          throw new Error(`Failed to fetch currencyOverview.json: ${res.status}`);
        }
        return res.json();
      })
      .then((ninjaData) => {
        console.log("Fetched Ninja Data:", ninjaData);

        // Build the icon map
        const map = {};
        if (ninjaData && ninjaData.currencyDetails) {
          ninjaData.currencyDetails.forEach((c) => {
            map[c.name] = c.icon;
          });
        }
        setCurrencyIconMap(map);

        // 2) Second fetch: prices.json (for local exchange data)
        return fetch("/faustus-price-checker/faustusPrices.json");
      })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`Failed to fetch faustusPrices.json: ${res.status}`);
        }
        return res.json();
      })
      .then((pricesData) => {
        console.log("Fetched Prices Data:", pricesData);

        // Validate the data structure
        if (!pricesData || !pricesData.exchanges) {
          throw new Error("Invalid data structure in faustusPrices.json");
        }

        // Flatten the "exchanges" into rows
        const flattenedRows = flattenExchanges(pricesData);
        setRows(flattenedRows);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching data:", err);
        setLoading(false);
        setRows([]); // Set empty rows on error
      });
  }, []);

  // Define columns for the DataGrid
  const columns = [
    {
      field: "haveCurrency",
      headerName: "Have Currency",
      width: 200,
      renderCell: (params) => {
        const currencyName = params.value;
        const iconUrl = currencyIconMap[currencyName];
        return (
          <div style={{ display: "flex", alignItems: "center" }}>
            {iconUrl && (
              <img
                src={iconUrl}
                alt={currencyName}
                style={{ width: 24, height: 24, marginRight: 8 }}
              />
            )}
            {currencyName}
          </div>
        );
      }
    },
    { field: "wantCurrency", headerName: "Want Currency", width: 130 },
    { field: "lastUpdated", headerName: "Last Updated", width: 180 },
    { field: "rowType", headerName: "Type", width: 100 },
    { field: "haveAmount", headerName: "Have Amt", type: "number", width: 100 },
    { field: "wantAmount", headerName: "Want Amt", type: "number", width: 100 },
    { field: "stock", headerName: "Stock", type: "number", width: 100 }
  ];

  if (loading) {
    return <div>Loading data...</div>;
  }

  return (
    <div style={{ height: 800, width: "100%" }}>
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={10}
        rowsPerPageOptions={[5, 10, 25]}
      />
    </div>
  );
}
