// src/components/PriceDataGrid.jsx
import React from "react";
import { DataGrid } from "@mui/x-data-grid";


const columns = [
    { field: "haveCurrency", headerName: "Have Currency", width: 130 },
    { field: "wantCurrency", headerName: "Want Currency", width: 130 },
    { field: "lastUpdated", headerName: "Last Updated", width: 180 },
    { field: "rowType", headerName: "Type", width: 90 },
    { field: "haveAmount", headerName: "Have Amt", type: "number", width: 100 },
    { field: "wantAmount", headerName: "Want Amt", type: "number", width: 100 },
    { field: "stock", headerName: "Stock", type: "number", width: 100 }
  ];

// Option A: Hardcode the data right here:
const priceData = {
  "updated": "2025-01-26T02:42:40.406188Z",
  "exchanges": [
    // ... entire JSON ...
  ]
};

// Flatten function (same as above)
function flattenExchanges(priceData) {
  let rowId = 1;
  const rows = [];

  priceData.exchanges.forEach((exchange) => {
    const { haveCurrency, wantCurrency, lastUpdated, offers, competingTrades } = exchange;

    offers.forEach((offer) => {
      rows.push({
        id: rowId++,
        haveCurrency,
        wantCurrency,
        lastUpdated,
        rowType: "offer",
        haveAmount: offer.haveAmount,
        wantAmount: offer.wantAmount,
        stock: offer.stock
      });
    });

    competingTrades.forEach((trade) => {
      rows.push({
        id: rowId++,
        haveCurrency,
        wantCurrency,
        lastUpdated,
        rowType: "competing",
        haveAmount: trade.haveAmount,
        wantAmount: trade.wantAmount,
        stock: trade.stock
      });
    });
  });

  return rows;
}

function PriceDataGrid() {
  // Flatten the data
  const rows = flattenExchanges(priceData);

  return (
    <div style={{ height: 600, width: "100%", marginTop: "1rem" }}>
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={10}       // how many rows per page
        rowsPerPageOptions={[5, 10, 25]}
        disableSelectionOnClick
      />
    </div>
  );
}

export default PriceDataGrid;
