import React, { useEffect, useState } from "react";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import { Box, CircularProgress, Chip } from "@mui/material";

function HeaderBar() {
  const [divineOrbPrice, setDivineOrbPrice] = useState(null);
  const [isActive, setIsActive] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkSiteStatus = async () => {
      try {
        // Fetch the currencyOverview.json file
        const response = await fetch("/currencyOverview.json");
        const data = await response.json();
  
        // Get the Divine Orb price from chaosEquivalent
        const divineOrb = data.lines.find(
          (line) => line.currencyTypeName === "Divine Orb"
        );
        const price = divineOrb ? divineOrb.chaosEquivalent : "N/A";
        setDivineOrbPrice(price);
  
        // Fetch the last modified time of the file
        const fileResponse = await fetch("/currencyOverview.json", {
          method: "HEAD",
        });
        const lastModified = fileResponse.headers.get("last-modified");
        console.log(fileResponse.headers);
        const lastModifiedDate = new Date(lastModified);
  
        // Convert to PST
        const lastModifiedPST = new Intl.DateTimeFormat("en-US", {
          timeZone: "America/Los_Angeles",
          year: "numeric",
          month: "long",
          day: "numeric",
          hour: "2-digit",
          minute: "2-digit",
          second: "2-digit",
        }).format(lastModifiedDate);
  
        console.log("Last Modified Date (PST):", lastModifiedPST);
  
        // Check if the file is recent (last 6 hours)
        const now = new Date();
        const sixHoursAgo = new Date(now.getTime() - 6 * 60 * 60 * 1000);
        setIsActive(lastModifiedDate > sixHoursAgo);
  
        setLoading(false);
      } catch (error) {
        console.error("Error checking site status:", error);
        setLoading(false);
      }
    };
  
    checkSiteStatus();
  }, []);

  if (loading) {
    return (
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Faustus Price Checker
          </Typography>
          <CircularProgress color="inherit" size={24} />
        </Toolbar>
      </AppBar>
    );
  }

  return (
    <AppBar position="static">
      <Toolbar>
        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          Faustus Price Checker
        </Typography>
        {divineOrbPrice && (
          <Box sx={{ marginRight: 2 }}>
            <Typography variant="body1">
              Divine Orb Price: {divineOrbPrice} Chaos Orbs
            </Typography>
          </Box>
        )}
        <Chip
          label={isActive ? "Active" : "Inactive"}
          color={isActive ? "success" : "error"}
          sx={{ fontWeight: "bold" }}
        />
      </Toolbar>
    </AppBar>
  );
}

export default HeaderBar;
